import React, { Component } from 'react'
import LoginPage from './LoginPage'
import SignUp from './SignUp'

export class Selector extends Component {

    constructor(props) {
        super(props)

        this.state = {
             showLogin : true
        }

        this.childSwitcher = this.childSwitcher.bind(this)
    }

    childSwitcher(){
        const switchLogin = !this.state.showLogin
        console.log("parent/childSwitcher")
        this.setState({
            showLogin : switchLogin
        })
    }


    render() {
        return (
            <div>
                {this.state.showLogin ? <LoginPage childSwitcher = {this.childSwitcher}/>
                : <SignUp />}
            </div>
        )
    }
}

export default Selector
