import React, { Component } from 'react'
import LoginPage from './LoginPage'
import SignUp from './SignUp'

export class Selector extends Component {

    constructor(props) {
        super(props)

        this.state = {
             showLogin : true,
             users: [
                 {email: "testuser@test.com", password : "test"}
             ]
        }

        this.childSwitcher = this.childSwitcher.bind(this)
    }

    childSwitcher(){
        const switchLogin = !this.state.showLogin
        console.log("parent/childSwitcher")
        this.setState({
            showLogin : switchLogin
        })
    }

    weHaveUser(emailToCheck){
        const answer = this.state.users.filter()
        function checker(user){
            return user.email == emailToCheck
        }
        return answer
    }


    render() {
        return (
            <div>
                {this.state.showLogin ? <LoginPage childSwitcher = {this.childSwitcher}/>
                : <SignUp childSwitcher = {this.childSwitcher}/>}
            </div>
        )
    }
}

export default Selector
