import React, { Component } from 'react'

export class SignUp extends Component {
    constructor(props) {
        super(props)

        this.state = {
            finalData: {
                email: "",
                password : ""             
            },
            tempData:{
                email: "",
                pass1: "",
                pass2: "",
                agreed: false
            }
        }
        this.switchToSignUp = this.switchToSignUp.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    switchToSignUp(){
        this.props.childSwitcher()
    }

    handleChange(event){
        //valami ilyesmi kell majd
        let copiedTempData = this.state.tempData

        switch (event.target.type){
            case "email":
                copiedTempData.email = event.target.value
                this.setState({finalData: copiedTempData})
                console.log(this.state)
                break  
            case "password":
                copiedTempData.pass1 = event.target.value
                this.setState({finalData: copiedTempData})   
                console.log(this.state)     
                break
            default:
                console.log("ohh")          
        }
    }

    handleSubmit(event){
        console.log(this.state.finalData)
        this.props.signYouUp(this.state.finalData)
        event.preventDefault()
    }

    render() {
        return (
            <div>
                <h1>Sign Up</h1>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Register to wonderPage!
                        <br />
                        Your email: <br />
                        <input 
                            type="email" 
                            name="email"
                            value={this.state.email} 
                            onChange={this.handleChange}/>
                        <br />
                        Choose a password: <br />
                        <input 
                            type="password"
                            name="pass1" 
                            value={this.state.password} 
                            onChange={this.handleChange} />
                        <br />
                        <input 
                            type="submit" 
                            value="Register" />
                    </label>
                </form>
                <form onSubmit={this.switchToSignUp}>
                    <label>
                        Already registered? &nbsp;
                        <input type="submit" value="Log in"/>
                    </label>
                </form>
            </div>
        )
    }
}

export default SignUp
