import React, { Component } from 'react'

export class LoginPage extends Component {
    constructor(props) {
        super(props)

        this.state = {
            email: "",
            password : ""             
        }
        this.switchToLogin = this.switchToLogin.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    switchToLogin(){
        this.props.childSwitcher()
    }

    handleChange(event){
        switch (event.target.type){
            case "email":
                this.setState({email: event.target.value.toLowerCase()})
                break  
            case "password":
                this.setState({password: event.target.value})        
                break
            default:
                console.log("ohh")          
        }


    }

    handleSubmit(event){
        this.props.signYouIn(this.state)
        event.preventDefault()
    }

    render() {
        const customCSS = {
            form: "",
            inputs: "form-control",
            texts: "form-text",
            button: "btn btn-primary"
        }
        return (
            <div>
                <h1>Login</h1>
                <form onSubmit={this.handleSubmit} >
                    <label class={customCSS.texts}>
                        Log in to wonderPage!
                        <br />
                        Your email: <input type="email" value={this.state.email} onChange={this.handleChange} class={customCSS.inputs}/>
                        <br />
                        Password: <input type="password" value={this.state.password} onChange={this.handleChange} class={customCSS.inputs}/>
                        <br />
                        <input type="submit" value="Log in" class={customCSS.button}/>
                    </label>
                </form>
                <form onSubmit={this.switchToLogin}>
                    <label >
                        <div class={customCSS.texts}>Haven't registered yet? </div>
                        <input type="submit" value="Register" class={customCSS.button}/>
                    </label>
                </form>
            </div>
        )
    }
}

export default LoginPage
